

DROP TABLE IF EXISTS Dados;
CREATE TABLE Dados (
    idTrabalho INT,
    numeroEstande INT,
    grupoTrabalho VARCHAR(50),
    curTrabalho VARCHAR(50),
    idAluno INT PRIMARY KEY,
    nomeAluno VARCHAR(100),
    raAluno VARCHAR(20),
    curAluno VARCHAR(50),
    idOrientador INT,
    nomeOrientador VARCHAR(100),
    rfOrientador VARCHAR(20),
    tituloTrabalho VARCHAR(255),
    descricaoTrabalho TEXT,
    anoLetivo INT
);



